import React, { useEffect } from 'react';
import './App.css';
import L from 'leaflet';
import * as d3 from 'd3';

function App() {
  useEffect(() => {
    // Leaflet map initialization
    const map = L.map('map', {
      zoomControl: false,
      scrollWheelZoom: false,
      doubleClickZoom: false,
      dragging: false,
      maxBounds: [[-90, -180], [90, 180]],
      maxBoundsViscosity: 1.0,
    }).setView([40, 0], 1.5);

    // Create a pane for the background layer
    map.createPane('backgroundPane');
    map.getPane('backgroundPane').style.zIndex = 100;

    L.rectangle([[-90, -180], [90, 180]], {
      color: '#000000',
      fillColor: '#000000',
      fillOpacity: 1,
      pane: 'backgroundPane',
    }).addTo(map);

    fetch('https://raw.githubusercontent.com/datasets/geo-countries/master/data/countries.geojson')
      .then((response) => response.json())
      .then((data) => {
        L.geoJSON(data, {
          style: {
            color: '#4A90E2',
            weight: 0.5,
            fillColor: '#000000',
            fillOpacity: 0.5,
          },
          onEachFeature: function (feature, layer) {
            layer.on({
              mouseover: function () {
                layer.setStyle({
                  color: '#4A90E2',
                  weight: 1.5,
                  dashArray: '5, 5',
                  fillColor: '#4A90E2',
                  fillOpacity: 0.5,
                });
              },
              mouseout: function () {
                layer.setStyle({
                  color: '#4A90E2',
                  weight: 0.5,
                  dashArray: '',
                  fillColor: '#000000',
                  fillOpacity: 0.5,
                });
              },
            });
          },
        }).addTo(map);
      });

    // Attack simulation
    let attackData = [

    ];  // Add attack data here
    let currentIndex = 0;

    const svgLayer = d3.select(map.getPanes().overlayPane).append("svg"),
      g = svgLayer.append("g").attr("class", "leaflet-zoom-hide");

    function reset() {
      const bounds = map.getBounds(),
        topLeft = map.latLngToLayerPoint(bounds.getNorthWest()),
        bottomRight = map.latLngToLayerPoint(bounds.getSouthEast());

      svgLayer
        .attr('width', bottomRight.x - topLeft.x)
        .attr('height', bottomRight.y - topLeft.y)
        .style('left', `${topLeft.x}px`)
        .style('top', `${topLeft.y}px`);

      g.attr('transform', `translate(${-topLeft.x}, ${-topLeft.y})`);

      showNextAttack();
    }

    function showNextAttack() {
      if (currentIndex >= attackData.length) currentIndex = 0;

      const attack = attackData[currentIndex];
      currentIndex++;
      const source = projectPoint(attack.source);
      const destination = projectPoint(attack.destination);
      const midPoint = [(source[0] + destination[0]) / 2 + 100, (source[1] + destination[1]) / 2];

      let lineColor;
      switch (attack.threatType) {
        case 'malware':
          lineColor = 'red';
          break;
        case 'phishing':
          lineColor = 'purple';
          break;
        case 'exploit':
          lineColor = 'yellow';
          break;
        default:
          lineColor = 'white';
      }

      const lineGenerator = d3.line()
        .curve(d3.curveBundle.beta(1))
        .x(d => d[0])
        .y(d => d[1]);

      const path = g.append("path")
        .datum([source, midPoint, destination])
        .attr("class", "attack-line")
        .attr("d", lineGenerator)
        .attr("stroke", lineColor)
        .attr("stroke-width", 2)
        .attr("stroke-dasharray", function () {
          return this.getTotalLength();
        })
        .attr("stroke-dashoffset", function () {
          return this.getTotalLength();
        })
        .transition()
        .duration(1000)
        .ease(d3.easeLinear)
        .attr("stroke-dashoffset", 0)
        .on("end", function () {
          d3.select(this).remove();
          showNextAttack();
        });

      d3.select("#activeAttacksList").append("li").text(`${attack.sourceName} ➔ ${attack.destinationName} (${attack.threatType})`).transition().duration(1000).remove();
    }

    function projectPoint(latlng) {
      const point = map.latLngToLayerPoint(new L.LatLng(latlng[0], latlng[1]));
      return [point.x, point.y];
    }

    document.getElementById('updateAttacks').addEventListener('click', () => {
      showNextAttack();
    });

    map.on("moveend", reset);
    reset();
  }, []);

  return (
    <div className="App">
      <header id="header">
        <img id="logo" src="polysialogo.png" alt="Logo" />
        <h4 id="logotitle">Polysia Tech</h4>
        <h1>ThreatMap Live Attack</h1>
      </header>

      <div id="container">
        <div id="leftSidebar">
          <h2>Threat Data</h2>
          <ul id="threatList">
            <li>
              <b>Number of Attacks:</b>
              <input type="number" id="attackCount" min="1" max="30" defaultValue="3" />
              <button id="updateAttacks">Update</button>
            </li>
            <li>
              <b>Active Attacks:</b>
              <ul id="activeAttacksList"></ul>
            </li>
          </ul>
        </div>
        <div id="map" />
      </div>
    </div>
  );
}

export default App;
