import React, { useEffect } from 'react';
import * as L from 'leaflet';
import * as d3 from 'd3';
import { initializeMap } from '../script';

const MapComponent = ({ attackData }) => {
  useEffect(() => {
    const map = L.map('map', {
      zoomControl: false,
      scrollWheelZoom: false,
      doubleClickZoom: false,
      dragging: false,
      maxBounds: [[-90, -180], [90, 180]],
      maxBoundsViscosity: 1.0,
    }).setView([40, 0], 1.5);

    initializeMap(map, attackData); // Calling map logic from external script

    return () => {
      map.remove();
    };
  }, [attackData]);

  return <div id="map"></div>;
};

export default MapComponent;
