import React, { useEffect } from 'react';
import * as d3 from 'd3';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

const attackData = [
  { id: 1, source: [37.7749, -122.4194], destination: [40.7128, -74.0060], sourceName: 'USA', destinationName: 'USA', threatType: 'malware' },
  { id: 2, source: [51.5074, -0.1278], destination: [48.8566, 2.3522], sourceName: 'UK', destinationName: 'France', threatType: 'phishing' },
  { id: 3, source: [35.6895, 139.6917], destination: [37.5665, 126.9780], sourceName: 'Japan', destinationName: 'South Korea', threatType: 'exploit' },
  { id: 4, source: [-33.9249, 18.4241], destination: [28.6139, 77.2090], sourceName: 'South Africa', destinationName: 'India', threatType: 'malware' },
  { id: 5, source: [34.0522, -118.2437], destination: [-33.8688, 151.2093], sourceName: 'USA', destinationName: 'Australia', threatType: 'phishing' },
  { id: 6, source: [55.7558, 37.6176], destination: [28.6139, 77.2090], sourceName: 'Russia', destinationName: 'India', threatType: 'exploit' },
  { id: 7, source: [28.6139, 77.2090], destination: [55.7558, 37.6176], sourceName: 'India', destinationName: 'Russia', threatType: 'malware' },
  { id: 8, source: [37.7749, -122.4194], destination: [55.7558, 37.6176], sourceName: 'USA', destinationName: 'Russia', threatType: 'phishing' },
];

const MapComponent = () => {
  useEffect(() => {
    const map = L.map('map', {
      zoomControl: false,
      scrollWheelZoom: false,
      doubleClickZoom: false,
      dragging: false,
      maxBounds: [[-90, -180], [90, 180]],
      maxBoundsViscosity: 1.0,
    }).setView([40, 0], 1.5);

    L.rectangle([[-90, -180], [90, 180]], {
      color: '#000000',
      fillColor: '#000000',
      fillOpacity: 1,
    }).addTo(map);

    fetch('https://raw.githubusercontent.com/datasets/geo-countries/master/data/countries.geojson')
      .then((response) => response.json())
      .then((data) => {
        L.geoJSON(data, {
          style: {
            color: '#4A90E2',
            weight: 0.5,
            fillColor: '#000000',
            fillOpacity: 0.5,
          },
          onEachFeature: function (feature, layer) {
            layer.on({
              mouseover: function () {
                layer.setStyle({
                  color: '#4A90E2',
                  weight: 1.5,
                  dashArray: '5, 5',
                  fillColor: '#4A90E2',
                  fillOpacity: 0.5
                });
              },
              mouseout: function () {
                layer.setStyle({
                  color: '#4A90E2',
                  weight: 0.5,
                  dashArray: '',
                  fillColor: '#000000',
                  fillOpacity: 0.5
                });
              }
            });
          },
        }).addTo(map);
      });

    const svgLayer = d3.select(map.getPanes().overlayPane).append('svg'),
      g = svgLayer.append('g').attr('class', 'leaflet-zoom-hide');

    let currentIndex = 0;

    const projectPoint = (latlng) => {
      const point = map.latLngToLayerPoint(new L.LatLng(latlng[0], latlng[1]));
      return [point.x, point.y];
    };

    const showNextAttack = () => {
      if (currentIndex >= attackData.length) currentIndex = 0;

      const attack = attackData[currentIndex];
      currentIndex++;

      const source = projectPoint(attack.source);
      const destination = projectPoint(attack.destination);
      const midPoint = [
        (source[0] + destination[0]) / 2 + 100,
        (source[1] + destination[1]) / 2,
      ];

      let lineColor;
      switch (attack.threatType) {
        case 'malware':
          lineColor = 'red';
          break;
        case 'phishing':
          lineColor = 'purple';
          break;
        default:
          lineColor = 'white';
      }

      const lineGenerator = d3.line()
        .curve(d3.curveBundle.beta(1))
        .x((d) => d[0])
        .y((d) => d[1]);

      const path = g.append('path')
        .datum([source, midPoint, destination])
        .attr('class', 'attack-line')
        .attr('d', lineGenerator)
        .attr('stroke', lineColor)
        .attr('stroke-width', 2)
        .attr('fill', 'none');

      // Animate the attack line
      path.transition()
        .duration(1000)
        .ease(d3.easeLinear)
        .attr('stroke-dasharray', function() {
          const length = this.getTotalLength();
          return `${length} ${length}`;
        })
        .attr('stroke-dashoffset', 0)
        .on('end', () => {
          path.remove(); // Remove the path after animation
          showNextAttack(); // Show the next attack
        });

      // Append attack information to the active attacks list
      const attackInfo = `${attack.sourceName} âž” ${attack.destinationName} (${attack.threatType})`;
      const listItem = d3.select('#activeAttacksList').append('li').text(attackInfo);
      listItem.transition().duration(1000).remove(); // Automatically remove after 1 second
    };

    showNextAttack();

    return () => {
      map.remove(); // Cleanup map instance
    };
  }, []);

  return (
    <div>
      {/* Header Section */}
      <header style={{ backgroundColor: '#333', color: '#fff', padding: '10px', textAlign: 'center' }}>
        <h1>Global Attack Visualization</h1>
      </header>

      {/* Map and Active Attacks List */}
      <div style={{ position: 'relative' }}>
        <div id="map" style={{ height: 'calc(100vh - 60px)', marginTop: '0px' }}></div>
        <ul id="activeAttacksList" style={{ position: 'absolute', top: 10, left: 10, zIndex: 1000, color: 'white', background: 'rgba(0, 0, 0, 0.7)', padding: '10px', borderRadius: '5px' }}></ul>
      </div>
    </div>
  );
};

export default MapComponent;
