];

const MapComponent = () => {
  useEffect(() => {
    const map = L.map('map', {
      zoomControl: false,
      scrollWheelZoom: false,
      doubleClickZoom: false,
      dragging: false,
      maxBounds: [[-90, -180], [90, 180]],
      maxBoundsViscosity: 1.0,
    }).setView([40, 0], 1.5);

    L.rectangle([[-90, -180], [90, 180]], {
      color: '#000000',
      fillColor: '#000000',
      fillOpacity: 1,
    }).addTo(map);

    fetch('https://raw.githubusercontent.com/datasets/geo-countries/master/data/countries.geojson')
      .then((response) => response.json())
      .then((data) => {
        L.geoJSON(data, {
          style: {
            color: '#4A90E2',
            weight: 0.5,
            fillColor: '#000000',
            fillOpacity: 0.5,
          },
          onEachFeature: (feature, layer) => {
            layer.on({
              mouseover: () => layer.setStyle({ color: '#4A90E2', weight: 1.5 }),
              mouseout: () => layer.setStyle({ color: '#4A90E2', weight: 0.5 }),
            });
          },
        }).addTo(map);
      });

    const svgLayer = d3.select(map.getPanes().overlayPane).append('svg'),
      g = svgLayer.append('g').attr('class', 'leaflet-zoom-hide');

    let currentIndex = 0;

    const showNextAttack = () => {
      if (currentIndex >= attackData.length) currentIndex = 0;

      const attack = attackData[currentIndex];
      currentIndex++;

      const source = projectPoint(map, attack.source);
      const destination = projectPoint(map, attack.destination);
      const midPoint = [
        (source[0] + destination[0]) / 2 + 100,
        (source[1] + destination[1]) / 2,
      ];

      let lineColor;
      switch (attack.threatType) {
        case 'malware':
          lineColor = 'red';
          break;
        case 'phishing':
          lineColor = 'purple';
          break;
        default:
          lineColor = 'white';
      }

      const lineGenerator = d3.line().curve(d3.curveBundle.beta(1)).x((d) => d[0]).y((d) => d[1]);

      g.append('path')
        .datum([source, midPoint, destination])
        .attr('class', 'attack-line')
        .attr('d', lineGenerator)
        .attr('stroke', lineColor)
        .attr('stroke-width', 2)
        .transition()
        .duration(1000)
        .ease(d3.easeLinear)
        .attr('stroke-dashoffset', 0)
        .on('end', () => showNextAttack());

      const attackInfo = `${attack.sourceName} ➔ ${attack.destinationName} (${attack.threatType})`;
      d3.select('#activeAttacksList').append('li').text(attackInfo).transition().duration(1000).remove();
    };

    showNextAttack();
  }, []);

  const projectPoint = (map, latlng) => {
    const point = map.latLngToLayerPoint(new L.LatLng(latlng[0], latlng[1]));
    return [point.x, point.y];
  };

  return <div id="map"></div>;
};

export default MapComponent;
