import React, { useEffect, useState } from 'react';
import * as d3 from 'd3';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

const MapComponent = () => {
  const [attackData, setAttackData] = useState([]);
  const [loading, setLoading] = useState(true); // Add loading state

  useEffect(() => {
    // Fetch the attack data from the JSON file
    fetch('./attackData.json') // Adjust the path if needed
      .then(response => response.json())
      .then(data => {
        setAttackData(data); // Set the fetched data to state
        setLoading(false); // Update loading state
      })
      .catch(error => {
        console.error('Error fetching attack data:', error);
        setLoading(false); // Update loading state even if there's an error
      });

    // Initialize the map
    const map = L.map('map', {
      zoomControl: false,
      scrollWheelZoom: false,
      doubleClickZoom: false,
      dragging: false,
      maxBounds: [[-90, -180], [90, 180]],
      maxBoundsViscosity: 1.0
    }).setView([40, 0], 1.5);

    // Create a pane for the background layer
    map.createPane('backgroundPane');
    map.getPane('backgroundPane').style.zIndex = 100;

    // Add a solid-colored rectangle to cover the entire map
    L.rectangle([[-90, -180], [90, 180]], {
      color: '#000000',
      fillColor: '#000000',
      fillOpacity: 1,
      pane: 'backgroundPane'
    }).addTo(map);

    // Fetch and add geoJSON data to the map
    fetch('https://raw.githubusercontent.com/datasets/geo-countries/master/data/countries.geojson')
      .then(response => response.json())
      .then(data => {
        L.geoJSON(data, {
          style: {
            color: '#4A90E2',
            weight: 0.5,
            fillColor: '#000000',
            fillOpacity: 0.5
          },
          onEachFeature: function (feature, layer) {
            layer.on({
              mouseover: function () {
                layer.setStyle({
                  color: '#4A90E2',
                  weight: 1.5,
                  dashArray: '5, 5',
                  fillColor: '#4A90E2',
                  fillOpacity: 0.5
                });
              },
              mouseout: function () {
                layer.setStyle({
                  color: '#4A90E2',
                  weight: 0.5,
                  dashArray: '',
                  fillColor: '#000000',
                  fillOpacity: 0.5
                });
              }
            });
          }
        }).addTo(map);
      });

    // Create an SVG layer on top of Leaflet
    const svgLayer = d3.select(map.getPanes().overlayPane).append("svg");
    const g = svgLayer.append("g").attr("class", "leaflet-zoom-hide");

    let currentIndex = 0;

    // Define the projection from geographical coordinates (lat, lon) to screen points
    function projectPoint(latlng) {
      const point = map.latLngToLayerPoint(new L.LatLng(latlng[0], latlng[1]));
      return [point.x, point.y];
    }

    // Function to redraw the attack lines and reposition the SVG
    function reset() {
      const bounds = map.getBounds(),
        topLeft = map.latLngToLayerPoint(bounds.getNorthWest()),
        bottomRight = map.latLngToLayerPoint(bounds.getSouthEast());

      svgLayer
        .attr('width', bottomRight.x - topLeft.x)
        .attr('height', bottomRight.y - topLeft.y)
        .style('left', `${topLeft.x}px`)
        .style('top', `${topLeft.y}px`);

      g.attr('transform', `translate(${-topLeft.x}, ${-topLeft.y})`);

      showNextAttack();
    }

    // Function to display the next attack after the previous one reaches the destination
    function showNextAttack() {
      if (currentIndex >= attackData.length) {
        currentIndex = 0; // Reset 
      }

      const attack = attackData[currentIndex];
      currentIndex++; // next attack

      const source = projectPoint(attack.source);
      const destination = projectPoint(attack.destination);

      const midPoint = [
        (source[0] + destination[0]) / 2 + 100,
        (source[1] + destination[1]) / 2
      ];

      // Set color based on threat type
      let lineColor;
      switch (attack.threatType) {
        case 'malware':
          lineColor = 'red';
          break;
        case 'phishing':
          lineColor = 'purple';
          break;
        case 'exploit':
          lineColor = 'yellow';
          break;
        default:
          lineColor = 'white';
      }

      // Create line generator for curved path
      const lineGenerator = d3.line()
        .curve(d3.curveBundle.beta(1))
        .x(d => d[0])
        .y(d => d[1]);

      // Add attack line to SVG
      const path = g.append("path")
        .datum([source, midPoint, destination])
        .attr("class", "attack-line")
        .attr("d", lineGenerator)
        .style("fill", "none")
        .style("stroke", lineColor)
        .style("stroke-width", 2)
        .style("opacity", 0.8);

      // Animate the attack line
      path.transition()
        .duration(2000)
        .attrTween("stroke-dasharray", function () {
          const length = this.getTotalLength();
          return d3.interpolateString("0," + length, length + "," + length);
        })
        .on("end", () => {
          // Remove the path after animation
          path.remove();
          reset(); // Call reset to show the next attack
        });
    }

    map.on('resize', reset);

    reset();

    // Cleanup on component unmount
    return () => {
      map.off('resize', reset);
      map.remove(); 
    };
  }, [attackData]); // Dependency array includes attackData

  if (loading) {
    return <div>Loading...</div>; // Optional loading indicator
  }

  return <div id="map" style={{ height: '80vh' }}></div>; 
};

export default MapComponent;
