import React, { useEffect } from 'react';
import * as d3 from 'd3';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

const MapComponent = () => {
  useEffect(() => {
    // Initialize the map
    const map = L.map('map', {
      zoomControl: false,
      scrollWheelZoom: false,
      doubleClickZoom: false,
      dragging: false,
      maxBounds: [[-90, -180], [90, 180]],
      maxBoundsViscosity: 1.0
    }).setView([40, 0], 1.5);

    // Create a pane for the background layer
    map.createPane('backgroundPane');
    map.getPane('backgroundPane').style.zIndex = 100;

    // Add a solid-colored rectangle to cover the entire map
    L.rectangle([[-90, -180], [90, 180]], {
      color: '#000000',
      fillColor: '#000000',
      fillOpacity: 1,
      pane: 'backgroundPane'
    }).addTo(map);

    // Fetch and add geoJSON data to the map
    fetch('https://raw.githubusercontent.com/datasets/geo-countries/master/data/countries.geojson')
      .then(response => response.json())
      .then(data => {
        L.geoJSON(data, {
          style: {
            color: '#4A90E2',
            weight: 0.5,
            fillColor: '#000000',
            fillOpacity: 0.5
          },
          onEachFeature: function (feature, layer) {
            layer.on({
              mouseover: function () {
                layer.setStyle({
                  color: '#4A90E2',
                  weight: 1.5,
                  dashArray: '5, 5',
                  fillColor: '#4A90E2',
                  fillOpacity: 0.5
                });
              },
              mouseout: function () {
                layer.setStyle({
                  color: '#4A90E2',
                  weight: 0.5,
                  dashArray: '',
                  fillColor: '#000000',
                  fillOpacity: 0.5
                });
              }
            });
          }
        }).addTo(map);
      });

    // Create an SVG layer on top of Leaflet
    const svgLayer = d3.select(map.getPanes().overlayPane).append("svg"),
      g = svgLayer.append("g").attr("class", "leaflet-zoom-hide");

    // Define the attack data with source, destination, names, and threat type
    let attackData = [
      { id: 1, source: [37.7749, -122.4194], destination: [40.7128, -74.0060], sourceName: 'USA', destinationName: 'USA', threatType: 'malware' },
      // ... other attack data
      { id: 32, source: [34.0522, -118.2437], destination: [40.7128, -74.0060], sourceName: 'USA', destinationName: 'USA', threatType: 'malware' }
    ];

    let currentIndex = 0;

    // Define a default speed for the animation duration
    const defaultSpeed = 1000; // Duration in milliseconds

    // Define the projection from geographical coordinates (lat, lon) to screen points
    function projectPoint(latlng) {
      const point = map.latLngToLayerPoint(new L.LatLng(latlng[0], latlng[1]));
      return [point.x, point.y];
    }

    // Function to redraw the attack lines and reposition the SVG
    function reset() {
      const bounds = map.getBounds(),
        topLeft = map.latLngToLayerPoint(bounds.getNorthWest()),
        bottomRight = map.latLngToLayerPoint(bounds.getSouthEast());

      svgLayer
        .attr('width', bottomRight.x - topLeft.x)
        .attr('height', bottomRight.y - topLeft.y)
        .style('left', `${topLeft.x}px`)
        .style('top', `${topLeft.y}px`);

      g.attr('transform', `translate(${-topLeft.x}, ${-topLeft.y})`);

      showNextAttack();
    }

    // Function to display the next attack after the previous one reaches the destination
    function showNextAttack() {
      if (currentIndex >= attackData.length) {
        currentIndex = 0; // Reset if we reach the end of the attack data
      }

      const attack = attackData[currentIndex];
      currentIndex++; // Move to the next attack in the list

      const source = projectPoint(attack.source);
      const destination = projectPoint(attack.destination);

      // Mid-point for curvature
      const midPoint = [
        (source[0] + destination[0]) / 2 + 100,
        (source[1] + destination[1]) / 2
      ];

      // Set color based on threat type
      let lineColor;
      switch (attack.threatType) {
        case 'malware':
          lineColor = 'red';
          break;
        case 'phishing':
          lineColor = 'purple';
          break;
        case 'exploit':
          lineColor = 'yellow';
          break;
        default:
          lineColor = 'white';
      }

      // Create line generator for curved path
      const lineGenerator = d3.line()
        .curve(d3.curveBundle.beta(1))
        .x(d => d[0])
        .y(d => d[1]);

      // Add attack line to SVG
      const path = g.append("path")
        .datum([source, midPoint, destination])
        .attr("class", "attack-line")
        .attr("d", lineGenerator)
        .attr("stroke", lineColor)
        .attr("stroke-width", 2)
        .attr("stroke-dasharray", function () {
          return this.getTotalLength();
        })
        .attr("stroke-dashoffset", function () {
          return this.getTotalLength();
        })
        .transition()
        .duration(defaultSpeed)  // Use the default speed for each attack
        .ease(d3.easeLinear)
        .attr("stroke-dashoffset", 0)
        .on("end", function() {
          d3.select(this).remove(); // Remove the path after animation
          showNextAttack(); // Immediately start the next attack
        });

      // Add attack information to the sidebar
      const attackInfo = `${attack.sourceName} ➔ ${attack.destinationName} (${attack.threatType})`;
      d3.select("#activeAttacksList").append("li").text(attackInfo).transition().duration(1000).remove(); // Show info for a brief time
    }

    // Event listener for the attack count input
    document.getElementById('updateAttacks').addEventListener('click', () => {
      showNextAttack(); // Start or continue showing attacks
    });

    // Update the map on move end
    map.on("moveend", reset);
    reset(); // Start displaying attacks

    // Cleanup on component unmount
    return () => {
      map.off('moveend', reset);
      map.remove(); 
    };
  }, []);

  return (
    <div>
      <div id="map" style={{ width: '100%', height: '80vh' }}></div>
      <ul id="activeAttacksList"></ul> {/* Sidebar for displaying active attacks */}
      <button id="updateAttacks">Update Attacks</button>
    </div>
  );
};

export default MapComponent;
