import React, { useEffect } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

const MapComponent = () => {
  useEffect(() => {
    const map = L.map('map', {
      zoomControl: false,
      scrollWheelZoom: false,
      doubleClickZoom: false,
      dragging: false,
      maxBounds: [[-90, -180], [90, 180]],
      maxBoundsViscosity: 1.0
    }).setView([40, 0], 1.5);

    // Add background layer
    L.rectangle([[-90, -180], [90, 180]], {
      color: '#000000',
      fillColor: '#000000',
      fillOpacity: 1
    }).addTo(map);

    // Add geoJSON data
    fetch('https://raw.githubusercontent.com/datasets/geo-countries/master/data/countries.geojson')
      .then(response => response.json())
      .then(data => {
        L.geoJSON(data, {
          style: {
            color: '#4A90E2',
            weight: 0.5,
            fillColor: '#000000',
            fillOpacity: 0.5
          },
          onEachFeature: function (feature, layer) {
            layer.on({
              mouseover: function () {
                layer.setStyle({
                  color: '#4A90E2',
                  weight: 1.5,
                  dashArray: '5, 5',
                  fillColor: '#4A90E2',
                  fillOpacity: 0.5
                });
              },
              mouseout: function () {
                layer.setStyle({
                  color: '#4A90E2',
                  weight: 0.5,
                  dashArray: '',
                  fillColor: '#000000',
                  fillOpacity: 0.5
                });
              }
            });
          }
        }).addTo(map);
      });

    return () => {
      map.remove();
    };
  }, []);

  return (
    <div id="map" style={{ height: '100vh' }}></div>
  );
};

export default MapComponent;
